user_lesson_list_query = 'select app_lesson.id as id, app_lesson.name, app_lesson.order, app_lesson.description, completed, completed_at, lesson_id, user_id from app_lesson inner join app_userlesson on app_lesson.id = app_userlesson.lesson_id join auth_user on auth_user.id = app_userlesson.user_id where app_lesson.id in %s and auth_user.id = %s'

user_lesson_query = 'select app_lesson.id as id, app_lesson.name, app_lesson.order, app_lesson.description, completed, completed_at, lesson_id, user_id from app_lesson inner join app_userlesson on app_lesson.id = app_userlesson.lesson_id join auth_user on auth_user.id = app_userlesson.user_id where app_lesson.id = %s and auth_user.id = %s'